package com.stg.keke.activity;import java.text.SimpleDateFormat;import java.util.Date;import android.view.KeyEvent;import android.view.View;import com.stg.keke.widget.swipback.app.SwipeBackActivity;public class BaseActivity extends SwipeBackActivity {	protected static final int LODING_MORE = 0;	protected static final int REFRESH_MORE = 1;	@Override	public boolean onKeyDown(int keyCode, KeyEvent event) {		if (keyCode == KeyEvent.KEYCODE_BACK && event.getRepeatCount() == 0) {			scrollToFinishActivity();		}		return false;	}	public void terminate(View v) {		scrollToFinishActivity();	}	private final static ThreadLocal<SimpleDateFormat> dateFormater = new ThreadLocal<SimpleDateFormat>() {		@Override		protected SimpleDateFormat initialValue() {			return new SimpleDateFormat("MM-dd HH:mm");		}	};	protected String getCurrentTime() {		Date nowTime = new Date();		String nowTimeStr = dateFormater.get().format(nowTime);		return nowTimeStr;	}}