package com.stg.keke.activity;import java.text.SimpleDateFormat;import java.util.Date;import android.app.Activity;import android.view.KeyEvent;import android.view.View;import com.stg.keke.R;public class BaseSpecialActivity extends Activity {	protected static final int LODING_MORE = 0;	protected static final int REFRESH_MORE = 1;	@Override	public boolean onKeyDown(int keyCode, KeyEvent event) {		if (keyCode == KeyEvent.KEYCODE_BACK && event.getRepeatCount() == 0) {			finish();			overridePendingTransition(0, R.anim.out_from_right);		}		return false;	}	public void terminate(View v) {		finish();		overridePendingTransition(0, R.anim.out_from_right);	}	private final static ThreadLocal<SimpleDateFormat> dateFormater = new ThreadLocal<SimpleDateFormat>() {		@Override		protected SimpleDateFormat initialValue() {			return new SimpleDateFormat("MM-dd HH:mm");		}	};	protected String getCurrentTime() {		Date nowTime = new Date();		String nowTimeStr = dateFormater.get().format(nowTime);		return nowTimeStr;	}}